import { createStore } from "vuex";

const tabs = {
  1: "personalInfo",
  2: "plan",
  3: "addOns",
  4: "finishUp",
};

export default createStore({
  state: {
    displayedTab: 1,
    personalInfo: {
      name: "",
      email: "",
      phone: "",
      complete: false,
    },
    plan: {
      plan: "",
      yearly: false,
      complete: false,
    },
    addOns: {
      online: false,
      storage: false,
      custom: false,
      complete: false,
    },
    finishUp: {},
  },
  getters: {
    getCurrentTab(state) {
      let tabName = tabs[state.displayedTab];
      return state[tabName];
    },
  },
  mutations: {},
  actions: {},
  modules: {},
});
