import { createStore } from "vuex";

const tabs = {
  1: "personalInfo",
  2: "plan",
  3: "addOns",
  4: "finishUp",
};

const plans = {
  arcade: {
    mo: 9,
    yr: 90,
  },
  advanced: {
    mo: 12,
    yr: 120,
  },
  pro: {
    mo: 15,
    yr: 150,
  },
};

export default createStore({
  state: {
    displayedTabId: 1,
    personalInfo: {
      name: "",
      email: "",
      phone: "",
      complete: false,
    },
    plan: {
      plan: "",
      yearly: false,
      complete: false,
    },
    addOns: {
      online: false,
      storage: false,
      custom: false,
      complete: true,
    },
    finishUp: {},
  },
  getters: {
    getCurrentTab(state) {
      let tabName = tabs[state.displayedTabId];
      return state[tabName];
    },
    getPlan(state) {
      const planData = {};
      const planForm = state.plan;
      const planString = planForm.yearly ? "Yearly" : "Monthly";
      const priceSlug = planForm.yearly ? "yr" : "mo";
      planData.name = `${planForm.plan} (${planString})`;
      planData.price = `${plans[planForm.plan]}/${priceSlug}`;
      return planData;
    },
  },
  mutations: {
    updateDisplayedTab(state, payload) {
      state.displayedTabId = payload;
    },
    storeFormData(state, payload) {
      state[tabs[payload.tab]] = payload.data;
    },
  },
  actions: {},
  modules: {},
});
